import React from 'react';
import './Blog.css';

import Navbar from '../components/Navbar.jsx';
import Footer from '../components/Footer.jsx';

class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
          };
    }

    componentDidMount() {
        fetch("https://mbworksblog.firebaseio.com/blogdata.json")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }

      render() {
        const { error, isLoaded, items } = this.state;

        if (error) {
          return <div>Ошибка: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Загрузка...</div>;
        } else {
            const listItems = [items.length]
            console.log("hello")
            for (var i=0; i<items.length; i++){
                if (i%2 == 0) listItems[i] = <li className="leftCol" key={i}> <div>{items[i].title}</div> {items[i].text}</li> 
                else listItems[i] = <li className="rightCol" key={i}><div>{items[i].title}</div> {items[i].text}</li>
            }
            return(
                <div>
                    <Navbar/>

                        <ul>{listItems}</ul>
                    <Footer/>
                </div>
            );
        }
    }
}

export default Blog;
