import React from 'react';
import './Blog.css';

import Navbar from '../components/Navbar.jsx';
import Footer from '../components/Footer.jsx';

class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
          };
    }

    componentDidMount() {
        fetch("https://weekup-b2bae.firebaseio.com/new_templates/-LuhrVkmhwTVkxodgRks.json")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }

      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return <div>Ошибка: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Загрузка...</div>;
        } else {


            return(
                <div>
                    <Navbar/>
                    <ul>
                        {items.forEach(element => {
                            <li>{element.props("templateName")}</li>
                        })}
                    </ul>
                    <Footer/>
                </div>
            );
        }
    }
}

export default Blog;
