import React from 'react';
import "./BlogListItem.css";

function BlogListItem(sideClass, index, title, text){
    var isTextToggled = false;
    var showText;
    if (text.length > 100) {
        showText = text.substring(0,200);
        isTextToggled = true;
    }else{
        showText = {text};
    }

    return(
        <li className={sideClass} key={index}> 
            <div className="blogItemTitle">{title}</div> 
            <div id="blogItemText" onClick={() => {alert('You tapped the button!'); }}>{showText}</div>

        </li> 
    );

    function onTextClick(){
        if (isTextToggled){
            showText = text;
        }else{
            showText = text.substring(0,100);
        }
        var element = document.getElementById("blogItemText");
        element.value = showText;
        doEvent(element, 'input');
        isTextToggled = !isTextToggled;
    };

    function doEvent( obj, event ) {
        var event = new Event( event, {target: obj, bubbles: true} );
        return obj ? obj.dispatchEvent(event) : false;
    }
};

export default BlogListItem;
