import React from 'react';
import "./BlogListItem.css";

class BlogListItem extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            isTextToggled: false,
            text: text,
            showText: text,
            sideClass: sideClass,
            index: index,
            title: title
        };
        this.prepareText();
    }


    render() {
        return(
            <li className={this.state.sideClass} key={this.state.index}> 
                <div className="blogItemTitle">{this.state.title}</div> 
                <div id="blogItemText" onClick={() => {this.onTextClick(); }}>{this.state.showText}</div>

            </li> 
        );
    }


    prepareText(){
        if (this.state.text.length > 100) {
            this.state = {
                isTextToggled: true,
                text: this.state.text.substring(0,200)
            }; 
        };
    };

    onTextClick(){
        if (this.state.isTextToggled){
            this.state.showText = this.state.text;
        }else{
            this.state.showText = this.state.text.substring(0,100);
        }
        var element = document.getElementById("blogItemText");
        console.log(element+"   "+this.state.showText)

        element.value = this.state.showText;
        this.state.isTextToggled = !this.state.isTextToggled;
    };

};

export default BlogListItem;
