import React from 'react';
import mobiscroll from 'mobiscroll-react';
import firebase from '../Firebase';
import './Blog.css';

import Navbar from '../components/Navbar.jsx';
import Footer from '../components/Footer.jsx';

class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
          };
    }

    componentDidMount() {
      // fetch("https://mbworksblog.firebaseio.com/blogdata.json")
      //   .then(res => res.json())
      //   .then(
      //     (result) => {
      //       this.setState({
      //         isLoaded: true,
      //         items: result
      //       });
      //     },
      //     (error) => {
      //       this.setState({
      //         isLoaded: true,
      //         error
      //       });
      //     }
      //   )

      const db = firebase.firestore();
      var allDocs = []
      db.collection("blogData").get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          allDocs.push(doc.data());
        });
      })
      .catch((err) => {
        this.setState({
          isLoaded: true,
          error:err
        });
      });
      if(allDocs!=null){
        this.setState({
          isLoaded:true,
          items:allDocs
        })
      }
    }

    render() {
      const { error, isLoaded, items } = this.state;

      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
          return(
              <div>
                  <Navbar/>
                      <mobiscroll.ListView
                          itemType={ListItem}
                          data={items}
                      />

                  <Footer/>
              </div>
          );
      }
    } 
}

export default Blog;

class ListItem extends React.Component{
  render(){
    return <li>
        <h2>this.props.blogTitle</h2>
        <p>this.props.blogMessage</p>
    </li>
  }
}
