import React from 'react';
import './Blog.css';

import Navbar from '../components/Navbar.jsx';
import Footer from '../components/Footer.jsx';
import BlogListItem from '../components/BlogListItem';

class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
          };
    }

    componentDidMount() {
      fetch("https://mbworksblog.firebaseio.com/blogdata.json")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;

      if (error) {
        return <div>Ошибка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Загрузка...</div>;
      } else {
          const listItems = [items.length]
          for (var i=0; i<items.length; i++){
            if (i%2 == 0) listItems[i] = new BlogListItem().state = {
              isTextToggled: false,
              text: items[i].text,
              showText: items[i].text,
              sideClass: "leftCol",
              index: i,
              title: items[i].title
            };

            else listItems[i] = new BlogListItem().state = {
                isTextToggled: false,
                text: items[i].text,
                showText: items[i].text,
                sideClass: "rightCol",
                index: i,
                title: items[i].title
            };
          }
          return(
              <div>
                  <Navbar/>
                      <ul>{listItems}</ul>
                  <Footer/>
              </div>
          );
      }
    } 
}

export default Blog;
